mapper.select:
select *
from tourist_spot
where addr = #{category}
and
t_category = 0;

select * from tourist_spot
where t_category=#{t_category} 
order by num asc
limit #{pageStart}, #{perPageNum}

select * from tourist_spot where t_category=#{t_category} order by
totalCnt desc thumbCnt desc commentCnt desc
limit #{pageStart}, #{perPageNum}

select count(num)
from tourist_spot 
where t_category=#{t_category}
