mapper.select:
select license
from bcc_member
where id = #{id}

select grp_name
from plan
where num = #{num}

select leader
from plan
where num = #{num}

select name
from bcc_member
where id = #{id}

mapper.delete:
delete from grp_accept
where grp_num = #{grp_num}
and receiver = #{receiver}

mapper.select:
select count(*)
from plan_member
where grp_num = #{grp_num} and id = #{id}

select id
from plan_member
where grp_num = #{grp_num}
order by num asc;

mapper.update:
update plan
set leader = #{leader}
where num =	#{num}

update plan
set tour_date_start=#{tour_date_start}, tour_date_end=#{tour_date_end}, tour_plan=#{tour_plan}, writer=#{writer}, lastUpdate=now()
where num=#{num}
