mapper.update:
update bcc_member 
set email=#{email}, pw=#{pw}, tel=#{tel}
where id=#{id}

mapper.select:
select count(id and pw) 
from bcc_member 
where id = #{id} and pw = #{pw}

mapper.insert:
		insert into bcc_storage
		select * from bcc_member
        where id = #{id }

mapper.select:
select count(id) 
from bcc_member 
where id = #{id}

select count(email) 
from bcc_member 
where email = #{email}

select count(tel) 
from bcc_member 
where tel = #{tel}

mapper.update:
update bcc_member
set license=1
where id=#{id}

update bcc_member
set license=0
where id=#{id}

mapper.select:
select title,thumbnail,num from tourist_spot 
where num in (select b_num from thumb where m_id=#{keyword})
limit #{pageStart}, #{perPageNum}

select count(*)
from thumb
where m_id = #{id}
