mapper.select:
select num, title, thumbnail, lng, lat, t_category
from tourist_spot
where num = #{num}

mapper.update:
update plan
set writer = #{writer}, tour_plan = #{tour_plan}, tour_plan_acc = #{tour_plan_acc}, tour_date_start = #{tour_date_start}, tour_date_end = #{tour_date_end}, lastUpdate = now()
where num = #{num}
