mapper.select:
select license 
from bcc_member 
where id = #{id}

select grp_name
from bcc_plan
where num = #{num}

select leader
from bcc_plan
where num = #{num}

select name
from bcc_member
where id = #{id}

select count(*)
from bcc_plan_member
where grp_num = #{grp_num} and id = #{id}

select id
from bcc_plan_member
where grp_num = #{grp_num}
order by num asc;

mapper.update:
update bcc_plan
set leader = #{leader}
where num = #{num}
